```kotlin:app/src/main/java/com/serranoie/app/qrrounder/CameraHandlerViewModel.kt
package com.serranoie.app.qrrounder

import android.content.Context
import androidx.camera.core.CameraControl
import androidx.camera.core.CameraSelector.DEFAULT_BACK_CAMERA
import androidx.camera.core.FocusMeteringAction
import androidx.camera.core.Preview
import androidx.camera.core.SurfaceOrientedMeteringPointFactory
import androidx.camera.core.SurfaceRequest
import androidx.camera.lifecycle.ProcessCameraProvider
import androidx.camera.lifecycle.awaitInstance
import androidx.compose.ui.geometry.Offset
import androidx.lifecycle.LifecycleOwner
import androidx.lifecycle.ViewModel
import kotlinx.coroutines.awaitCancellation
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.StateFlow
import kotlinx.coroutines.flow.update

class CameraHandlerViewModel : ViewModel() {

 // used to set up a link between the Camera and your UI.
 private val _surfaceRequest = MutableStateFlow<SurfaceRequest?>(null)
 val surfaceRequest: StateFlow<SurfaceRequest?> = _surfaceRequest
 private var surfaceMeteringPointFactory: SurfaceOrientedMeteringPointFactory? = null
 private var cameraControl: CameraControl? = null

 private val cameraPreviewUseCase = Preview.Builder().build().apply {
 setSurfaceProvider { newSurfaceRequest ->
 _surfaceRequest.update { newSurfaceRequest }
 surfaceMeteringPointFactory = SurfaceOrientedMeteringPointFactory(
 newSurfaceRequest.resolution.width.toFloat(),
 newSurfaceRequest.resolution.height.toFloat()
 )
 }
 }

 suspend fun bindToCamera(appContext: Context, lifecycleOwner: LifecycleOwner) {
 val processCameraProvider = ProcessCameraProvider.awaitInstance(appContext)
 val camera = processCameraProvider.bindToLifecycle(
 lifecycleOwner, DEFAULT_BACK_CAMERA, cameraPreviewUseCase
 )
 cameraControl = camera.cameraControl

 // Cancellation signals we're done with the camera
 try {
 awaitCancellation()
 } finally {
 processCameraProvider.unbindAll()
 cameraControl = null
 }
 }

 fun tapToFocus(tapCoords: Offset) {
 val point = surfaceMeteringPointFactory?.createPoint(tapCoords.x, tapCoords.y)
 if (point != null) {
 val meteringAction = FocusMeteringAction.Builder(point).build()
 cameraControl?.startFocusAndMetering(meteringAction)
 }
 }
}
